-- Database di PRODOTTI COSMETICI

-- Creazione delle tabelle
CREATE TABLE brand (
    ID_brand INT AUTO_INCREMENT,
    nomeBrand VARCHAR(512),
    sitoBrand VARCHAR(512),
    PRIMARY KEY (ID_brand)
);

CREATE TABLE categoria (
    ID_categoria INT AUTO_INCREMENT,
    nomeCategoria VARCHAR(512),
    scopoCategoria VARCHAR(512),
    consigliCategoria VARCHAR(512),
    PRIMARY KEY (ID_categoria)
);

CREATE TABLE prodotto (
    ID_prodotto INT AUTO_INCREMENT,
    nomeProdotto VARCHAR(512),
    id_brand INT,
    id_categoria INT,
    prezzoProdotto FLOAT,
    votoProdotto FLOAT,
    ingredienti VARCHAR(512),
    pelliSensibili BOOLEAN,
    PRIMARY KEY (ID_prodotto),
    FOREIGN KEY (id_brand) REFERENCES brand(ID_brand),
    FOREIGN KEY (id_categoria) REFERENCES categoria(ID_categoria)
);

