-- Database di PROFUMI

-- Creazione delle tabelle
CREATE TABLE brand (
  ID_brand INT AUTO_INCREMENT,
  nomeBrand VARCHAR(512),
  fondazioneBrand INT,
  descrizioneBrand VARCHAR(512),
  PRIMARY KEY (ID_brand)
);

CREATE TABLE naso (
  ID_naso INT AUTO_INCREMENT,
  nomeNaso VARCHAR(512),
  cognomeNaso VARCHAR(512),
  nascitaNaso INT,
  morteNaso INT,
  PRIMARY KEY (ID_naso)
);

CREATE TABLE profumo (
  ID_profumo INT AUTO_INCREMENT,
  nomeProfumo VARCHAR(512),
  id_brand INT,
  annoProfumo INT,
  genere VARCHAR(512),
  id_naso INT,
  topNotes VARCHAR(512),
  middleNotes VARCHAR(512),
  baseNotes VARCHAR(512),
  PRIMARY KEY (ID_profumo),
  FOREIGN KEY (id_brand) REFERENCES brand(ID_brand),
  FOREIGN KEY (id_naso) REFERENCES naso(ID_naso)
);

